mapper.select:
SELECT c.name, a.title, a.author, a.view_count, a.post_date, a.edit_date, a.id, CASE WHEN f.id IS NOT NULL THEN 1 ELSE 0 END AS with_file FROM article a INNER JOIN category c ON a.category_id = c.id LEFT JOIN file f ON a.id = f.article_id WHERE 1=1
AND (title LIKE CONCAT('%', #{search.keyword}, '%') OR author LIKE CONCAT('%', #{search.keyword}, '%') OR content LIKE CONCAT('%', #{search.keyword}, '%'))
AND a.category_id = #{search.category}
AND a.post_date BETWEEN #{search.startDate} AND #{search.endDate}
ORDER BY a.post_date DESC limit #{paging.limit} offset #{paging.offset}

SELECT count(*) FROM article a WHERE 1=1
AND (title LIKE CONCAT('%', #{keyword}, '%') OR author LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))
AND a.category_id = #{category}
AND a.post_date BETWEEN #{startDate} AND #{endDate}
